import SketchRule from 'vue3-sketch-ruler';
import 'vue3-sketch-ruler/lib/style.css';
export default {
  name: 'SketchRuler',
  components: {
    SketchRule
  },
  props: {
    scale: {
      type: Number,
      default: 0
    },
    width: {
      type: Number,
      default: 0
    },
    height: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      showRuler: true,
      // scale: 2, // 658813476562495, //1,
      startX: 0,
      startY: 0,
      lines: {
        h: [],
        v: []
      },
      thick: 20,
      isShowRuler: true,
      isShowReferLine: true,
      palette: {
        bgColor: 'rgba(225,225,225, 0)',
        longfgColor: '#BABBBC',
        shortfgColor: '#C8CDD0',
        fontColor: '#7D8694',
        shadowColor: '#E8E8E8',
        lineColor: '#EB5648',
        borderColor: '#DADADC',
        cornerActiveColor: 'rgb(235, 86, 72, 0.6)'
      }
    };
  },
  watch: {
    // 重绘
    scale() {
      this.showRuler = false;
      setTimeout(() => {
        this.showRuler = true;
      }, 0);
    }
  }
};
