import "core-js/modules/es.array.push.js";
import BColorPicker from '@/components/BColorPicker.vue';
export default {
  name: 'ScrollTable2Setter',
  components: {
    BColorPicker
  },
  props: {
    attribute: {
      type: Object,
      default: () => {}
    }
  },
  data() {
    return {
      columns: [{
        colKey: 'colKey',
        title: '字段标识'
      }, {
        colKey: 'title',
        title: '字段名'
      }, {
        colKey: 'width',
        title: '宽度'
      }],
      modelShow: false,
      currentIndex: 0,
      textFamilyList: Object.freeze(['微软雅黑', '黑体', '宋体', '仿宋', '楷体', '华文宋体', '优设标题黑', 'SourceHanSans-Medium']),
      currentRow: {}
    };
  },
  methods: {
    confirmRow() {
      if (!this.currentRow.colKey || !this.currentRow.title) {
        this.$message.error('请输入字段标识或字段名称');
        return;
      }
      const id = new Date().getTime();
      // eslint-disable-next-line vue/no-mutating-props
      this.attribute.columns.push(Object.assign(this.currentRow, {
        id: id
      }));
      this.modelShow = false;
    },
    addCol() {
      this.currentRow = {
        width: 0
      };
      this.modelShow = true;
    },
    editRow(row) {
      this.currentRow = row;
      this.currentIndex = this.attribute.columns.indexOf(row);
      this.modelShow = true;
    },
    delCol() {
      // eslint-disable-next-line vue/no-mutating-props
      this.attribute.columns.splice(this.currentIndex, 1);
      this.modelShow = false;
    }
  }
};
