import { getDataJson, pollingRefresh } from '@/utils';
export default {
  name: 'BNum',
  props: {
    cptId: {
      type: String,
      default: ''
    },
    configProps: {
      type: Object,
      default: () => {}
    }
  },
  data() {
    return {
      cptData: {},
      uuid: null
    };
  },
  created() {
    this.uuid = require('uuid').v1();
    this.refreshCptData();
  },
  methods: {
    refreshCptData() {
      pollingRefresh(this.uuid, this.configProps.cptDataForm, this.loadData);
    },
    loadData() {
      getDataJson(this.configProps.cptDataForm, this.cptId).then(res => {
        this.cptData = res;
      });
    }
  }
};
